package com.redis.simple;import java.util.List;import redis.clients.jedis.Jedis;import com.redis.simple.base.BaseRedis;/** * RedisSortUtil * redis 排序 * @author Roger */public class RedisSortUtil extends BaseRedis {	private RedisSortUtil() {	}		public static Jedis getJedis(){		return BaseRedis.getJedis();	}		/**	 * 排序（安值从小到大排序）	 * 	 * @param key	 *            key	 * @return String	 */	public static List<String> sort(String key) {		Jedis jedis = getJedis();		List<String> data = jedis.sort(key);		returnJedis(jedis);		return data;	} }